                $(".pagemenu").css("padding-right", "17px");
                $(".pagetabs").css("padding-right", "17px");
            });

            $(jModal).on('hide.bs.modal', function (e) {
                $(".navbar").css("padding-right", "");
                $(".pagemenu").css("padding-right", "");
                $(".pagetabs").css("padding-right", "");
            });


            $(jModal).modal("show");
        }

