    function getParameterByName(name, url) {
        if (!url) url = window.location.href;
        name = name.replace(/[\[\]]/g, "\\$&");
        var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
            results = regex.exec(url);
        if (!results) return null;
        if (!results[2]) return '';
        return decodeURIComponent(results[2].replace(/\+/g, " "));
    }

    function getApiSamples(country, category) {
        var _country = '';
        var _category = '';
        var exportURL = '';

        if (country)
            _country = country.trim().toLowerCase();
        else if (TECountry)
            _country = TECountry.trim().toLowerCase();

        if (category)
            _category = category.trim().toLowerCase();
        else if (TECategory)
            _category = TECategory.trim().toLowerCase();

        var link = $('.export-btn-csv').attr('href');

        if (link) {
            if (link.indexOf('?format=csv') != -1)
                exportURL = link.replace('?format=csv', '');
            else
                exportURL = link.replace('&format=csv', '');

            var userKeyURL = exportURL.indexOf('?') != -1 ? '&c=Your_Key: Your_Secret' : '?c=Your_Key: Your_Secret';

            exportURL = exportURL + userKeyURL;
        }

        var apiCodeSamples = `

API URL:  ` + exportURL + `

Code examples: 

Python:


                        import tradingeconomics as te

                        te.login('Your_Key:Your_Secret') 

                        te.getIndicatorData(country='`+ _country + `', indicators='` + _category + `') 



jQuery: 


                        var url = '`+ exportURL + `'; 

                        $.ajax({

                        &nbsp&nbsp&nbsp&nbspurl: url, 

                        &nbsp&nbsp&nbsp&nbsptype: "GET", 

                        &nbsp&nbsp&nbsp&nbspdataType: 'json'

                        }).done(function (data) {

                                        &nbsp&nbsp&nbsp&nbspconsole.log(data); 

                        }); 

                    `;

        return apiCodeSamples;
    }

