	                                            + `




 `+
                                                    (text ? '
' + text + '' : '')
                                                + `




`+
                                                    (actionCallBtnText ? '' + actionCallBtnText + '' : '')
	                                            + `






Any question? Contact us







                        `

        // create the modal
        var modal = $(htmlModal);

        $(modal).modal('show');
    }

    function createPanel(actionCallBtnText, actionCallBtnLink, text, contactSubject, title) {
        var htmlPanel = `





`+
                                (title ? '
' + title + '' : '')
	                        + `




 `+
                                (text ? '
' + text + '' : '')
                            + `




`+
                                (actionCallBtnText ? '' + actionCallBtnText + '' : '')
	                        + `






Any question? Contact us


                    `

        return htmlPanel;
    }

    function getParameterByName(name, url) {
        if (!url) url = window.location.href;
        name = name.replace(/[\[\]]/g, "\\$&");
        var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
            results = regex.exec(url);
        if (!results) return null;
        if (!results[2]) return '';
        return decodeURIComponent(results[2].replace(/\+/g, " "));
    }

    function getApiSamples(country, category) {
        var _country = '';
        var _category = '';
        var exportURL = '';

        if (country)
            _country = country.trim().toLowerCase();
        else if (TECountry)
            _country = TECountry.trim().toLowerCase();

        if (category)
            _category = category.trim().toLowerCase();
        else if (TECategory)
            _category = TECategory.trim().toLowerCase();

        var link = $('.export-btn-csv').attr('href');

        if (link) {
            if (link.indexOf('?format=csv') != -1)
                exportURL = link.replace('?format=csv', '');
            else
                exportURL = link.replace('&format=csv', '');

            var userKeyURL = exportURL.indexOf('?') != -1 ? '&c=Your_Key: Your_Secret' : '?c=Your_Key: Your_Secret';

            exportURL = exportURL + userKeyURL;
        }

        var apiCodeSamples = `

API URL:  ` + exportURL + `

Code examples: 

Python:


                        import tradingeconomics as te

                        te.login('Your_Key:Your_Secret') 

                        te.getIndicatorData(country='`+ _country + `', indicators='` + _category + `') 



jQuery: 


                        var url = '`+ exportURL + `'; 

                        $.ajax({

                        &nbsp&nbsp&nbsp&nbspurl: url, 

                        &nbsp&nbsp&nbsp&nbsptype: "GET", 

                        &nbsp&nbsp&nbsp&nbspdataType: 'json'

                        }).done(function (data) {

                                        &nbsp&nbsp&nbsp&nbspconsole.log(data); 

                        }); 

                    `;

        return apiCodeSamples;
    }

console.log("AdSense Script");
function addScript(src, async, callback) {
    var js = document.createElement("script");
    js.type = "text/javascript";
    if (async) js.async = true;
    if (callback) js.onload = callback;
    js.src = src;
    document.body.appendChild(js);
}
$(document).ready(function () {
    if (window.location.pathname.indexOf("/calendar") > -1) {
        console.log("AdSense Not Loaded");
        return;
    }
    console.log("AdSense Document Ready");
    // Wait for one second to ensure the user started browsing
    setTimeout(function () {
        console.log("AdSense Timeout");
        (adsbygoogle = window.adsbygoogle || []);
        $("ins.adsbygoogle").each(function () {
            console.log('AdSense Found Ad');
            var s = document.createElement("script");
            s.type = "text/javascript";
            s.text = "(adsbygoogle = window.adsbygoogle || []).push({})";
            $(s).insertAfter($(this));
        });
        addScript("https://pagead2.googlesyndication.com/pagead/js/adsbygoogle.js", true);
    }, 100);
});

