    $(document).ready(function () {
        var TEChartUrl2 = "";
        var startDate = "";
        var endDate = "";
        var chartType = "";
        if (!isPostBack) {
            console.log("HISTORICAL CHART JS...");
            if ($('#ImageChart').length) {
