        function SetChart() {
            console.log("Updating Chart..............");
            $('#spinner').css("visibility", "visible");
            var src = TEChartUrl;
            if (startDate != "") src += "&d1=" + startDate;
            if (endDate != "") src += "&d2=" + endDate;
            if (chartType != "") src += "&" + chartType;
            if (TEChartUrl2 != "") src += "&url2=" + TEChartUrl2;

            if (IsMarket) {
                if (HasForecastChart || HasStatsChart) {
                    $('#chart').hide();
                    $('#historical').show();
                    $('#historical').collapse("show");
                    if (HasForecastChart) src += '&forecast=te';
                    else if (HasStatsChart) src += '&trend=1&ds=1';
                }
                else {
                    $('#chart').show();
                    $('#historical').hide();
                }
            }
            else if (HasForecastChart) {
                src += '&forecast=te';
                var category = '';
                if (TECategory != null) category = TECategory.toLowerCase().trim();
                if (src.indexOf("projection") == -1 && (category == 'population' || category == 'gdp')) src += '&projection=te';
            }
            else if (HasStatsChart) {
                src += '&trend=1&ds=1';
            }

            SetSrc(src);
            SetEmbed();

        }

        function SetSrc(src) {
            console.log("chart src:" + src);
            $('#ImageChart').attr("src", src);
            if (src.indexOf("ismobile=1") == -1) {
                var srcset = src + " 1x";
                srcset += ", " + src.replace(".png", "@2x.png") + " 2x";
                srcset += ", " + src.replace(".png", "@3x.png") + " 3x";
                $('#ImageChart').attr("srcset", srcset);
            }
        }
