        $('a[data-toggle="tab"]').on('shown.bs.tab', function (e) {

            var target = $(e.target).attr("href");
            console.log("Chart Tab Click => " + target);

            if (target == "#historical") {
                HasForecastChart = false;
                HasStatsChart = false;
                if ($(e.target).data('symbol')) {
                    var TESymbolChanged = $(this).data("symbol")
                    console.log("data-symbol: " + TESymbolChanged);
                    TEChartUrl = TEChartUrl.replace(TESymbol.toLowerCase(), TESymbolChanged.toLowerCase())
                    TESymbol = TESymbolChanged;
                }
                SetChart();

            }
            else if (target == "#forecast") {
                HasForecastChart = true;
                HasStatsChart = false;
                SetChart();
            }
            else if (target == "#stats") {
                HasStatsChart = true;
                HasForecastChart = false;
                SetChart();
            }
            else if (target == "#alerts") {
                //$('#AlertMePanel').collapse('show');
                if (!isAlertsLoaded) {
