                if (HasForecastChart || HasStatsChart) {
                    $('#chart').hide();
                    $('#historical').show();
                    $('#historical').collapse("show");
                    if (HasForecastChart) src += '&forecast=te';
                    else if (HasStatsChart) src += '&trend=1&ds=1';
                }
                else {
                    $('#chart').show();
                    $('#historical').hide();
                }
            }
            else if (HasForecastChart) {
                src += '&forecast=te';
                var category = '';
                if (TECategory != null) category = TECategory.toLowerCase().trim();
                if (src.indexOf("projection") == -1 && (category == 'population' || category == 'gdp')) src += '&projection=te';
            }
            else if (HasStatsChart) {
                src += '&trend=1&ds=1';
            }

            SetSrc(src);
            SetEmbed();

        }

