$(document).ready(function () {
    console.log('Shorten Text')
    var showChar = 300;
    var ellipsestext = "...";
    var moretext = "more";
    var lesstext = "less";
    $('.more').each(function () {
        var content = $(this).html();
        if (content.length > showChar) {
            var c = content.substr(0, showChar);
            var h = content.substr(showChar, content.length - showChar);
            var html = c + '' + ellipsestext + '' + h + ' ' + moretext + '';
            $(this).html(html);
        }
    });

    $(".morelink").click(function () {
        if ($(this).hasClass("less")) {
            $(this).removeClass("less");
            $(this).html(moretext);
        } else {
            $(this).addClass("less");
            $(this).html(lesstext);
        }
        $(this).parent().prev().toggle();
        $(this).prev().toggle();
        return false;
    });
});

    console.log("Stream JS");
    var streamBaseURL = '/ws/stream.ashx';
    var streamStart = 0;
    var streamSize = 20;
    var streamHeight = 1000;
    var streamPreviousTitle = '';
    var streamIsLoading = false;
    var IsTicker = true;
    if (window.location.pathname.indexOf('/stream') !== -1 ||
        window.location.pathname.indexOf('/news') !== -1)
        IsTicker = false;
    function getParameterByName(name, url) {
        if (!url) url = window.location.href;
        name = name.replace(/[\[\]]/g, "\\$&");
        var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
            results = regex.exec(url);
        if (!results) return null;
        if (!results[2]) return '';
        return decodeURIComponent(results[2].replace(/\+/g, " "));
    }
    function getItem(j, isPrepend) {
        if (j.title) {
            if (streamPreviousTitle != j.title) {
                if (!j.bgcolor) j.bgcolor = '#fff';
                if (!j.url) j.url = '/';

                var item = "
";

                if (IsTicker) {
                    item += "" + j.title + "";
                }
                else {
                    if (j.country) {
                        var countryColor = "#858585";
                        if (j.country.toLowerCase() == "united states") countryColor = '#0075b5';
                        else if (j.country.toLowerCase() == "united kingdom") countryColor = 'darkred';
                        item += '' + j.country + '';
                    }
                    if (j.category && j.category != j.country) {
                        var categoryColor = "#ddd";
                        item += ' ' + j.category + '';
                    }
                    item += "
";
                    item += "" + j.title + "";
                }
                if (j.html) item += "
" + j.html;
                else if (j.description) item += "
" + j.description;
                if (j.image) item += '
';
                if (j.diff) item += '
' + j.diff + '';
                item += '';

                streamPreviousTitle = j.title;

                if (isPrepend) {
                    $("#stream").prepend(item);
                    setTimeout(function () {
                        $("#" + j.ID).css("backgroundColor", '#fff');
                    }, 2000)

                    streamHeight = $("#stream").height();
                }

                return item;
            }
            else {
                console.log("Skipping Repeated Title: " + j.title);
                return null;
            }
        }
        else {
            console.log("Missing Title");
            return null;
        }
    }
    function getresult(start, size) {
        console.log("Stream getresult");
        var streamURL = streamBaseURL + "?start=" + start + "&size=" + size;

        if (IsTicker) {
            console.log("Stream for Ticker");
            start = start + 3;
            site = 10;
            streamURL = streamBaseURL + "?start=" + start + "&size=" + size;
            if (TECountry) streamURL += "&c=" + TECountry;
            if (TECategory) streamURL += "&i=" + TECategory;
        }
        else if (location.pathname.indexOf('/news') !== -1 && location.pathname.split('/').length == 3) {
            console.log("Stream for News");
            var sCountry = location.pathname.split('/')[1];
            if (sCountry) {
                sCountry = sCountry.replace('-', ' ');
                streamURL += "&c=" + sCountry;
            };
        }
        else if (location.pathname.indexOf('/stream') !== -1) {
            var sCategory = getParameterByName('i');
            if (sCategory) streamURL += "&i=" + sCategory;
            var sCountry = getParameterByName('c');
            if (sCountry) streamURL += "&c=" + sCountry;
        }
        else {
            return;
        }
        console.log(streamURL);
        $.ajax({
            url: streamURL,
            type: "GET",
            dataType: "json",
            beforeSend: function () {
                $('#spinner').show();
                streamIsLoading = true;
            },
            complete: function () {
                $('#spinner').hide();
                streamIsLoading = false;
            },
            success: function (data) {
                appendResult(data);
                if (start == 0) {
                    $('#stream-btn').css("visibility", "visible");
                    if (sCategory || sCountry) {
                        var sFilter = sCategory || sCountry;
                        if (sFilter == "economy") $('#streamTabEconomy').tab('show');
                        else if (sFilter == "markets") $('#streamTabMarkets').tab('show');
                        else {
                            //var sFilterDiv = '' + sFilter + ' 

';$("#stream").prepend(sFilterDiv);
                            $('#streamTabs').append('
' + sFilter + ' ');

                        }
                    }
                }
            },
            error: function () {
                console.error("Stream Error")
            }
        });
    }
    function appendResult(data) {
        console.log("Stream Appending Results");
        //console.log(data);
        var now = (new Date).getTime();
        var item = '';
        var items = [];
        $.each(data, function (i, j) {
            //console.log(j);
            var diff = '';
            var diffMs = now - new Date(j.date).getTime(); // Miliseconds
            var offset = new Date().getTimezoneOffset() * 60 * 1000;
            diffMs = diffMs + offset;
            if (diffMs < 0) {
                diff = 'few seconds ago';

            }
            else if (diffMs < 120000) {
                var diffS = Math.round(diffMs / 1000); // Seconds
                diff = diffS + ' seconds ago';
            }
            else if (diffMs < 7200000) {
                var diffMins = Math.round(diffMs / 60000); // minutes
                diff = diffMins + ' minutes ago';
            }
            else if (diffMs < 172800000) {
                var diffHrs = Math.round(diffMs / 3600000); // hours
                diff = diffHrs + ' hours ago';
            }
            else {
                var diffDays = Math.round(diffMs / 86400000); // days
                if (diffDays < 30) diff = diffDays + ' days ago';
                else diff = j.date;
            }
            //console.log(j.date, j.title, j.url, j.importance);
            j.diff = diff;
            j.bgcolor = '#fff';
            if (streamStart == 0) {
                j.bgcolor = '#cdebf9';
                //if (j.url) j.image = 'https://d3fy651gv2fhd3.cloudfront.net/charts/stream.png?h=200&w=300&lbl=0&url=' + j.url;
                var itemLatest = getItem(j, true);
            } else {
                j.append = true;
                item = getItem(j, false)
                if (item) items.push(item);
            }
            streamStart += 1;
        });
        $("", { "class": "list-group", html: items.join("") }).appendTo("#stream");
        streamHeight = $("#stream").height();
    }

    if (!IsTicker) {
        $(window).scroll(function () {

            if (streamHeight < 100000) {
                if ($(window).scrollTop() >= streamHeight * 0.80) {

                    if (!streamIsLoading) {
                        getresult(streamStart, streamSize);
                    }
                    else {
                        console.log("Stream Loading is Busy. Hold on ...")
                    }
                }
            }
        });
        getresult(streamStart, streamSize);
    }
