        });

        $('.pagetabs').on('click', '.page-scroll', function () {
            $('html, body').animate({
                scrollTop: $($.attr(this, 'href')).offset().top
            }, 500);
            return false;
        });

        $('.pagetabs').on('click', '.page-scroll-adjusted', function () {
            $('html, body').animate({
                scrollTop: $($.attr(this, 'href')).offset().top - 135
            }, 500);
            return false;
        });

