        isAlertsLoaded = true;
    }
    $('a[data-toggle="tab"]').on('shown.bs.tab', function (e) {
        if (!isAlertsLoaded) {
            var target = $(e.target).attr("href");
            if (target == "#alerts") {
                LoadAlertsScript();
            }
        }
    });

    if (!isAlertsLoaded && location.href.indexOf("#alerts") != -1) {
        LoadAlertsScript();
    }
