$(document).ready(function () {
    //financial table switch
    $('.markets-switch-js').find('a').on('click', function (e) {
        e.preventDefault();
        if ($(this).hasClass('active')) {
            return;
        }
        var toSetVisible = $(this).attr('data-type');

        if (toSetVisible == 'annual') {
            $('#ftable').hide();
            $('#ftable2').show();
            if (window.tradingChart && $(this).attr('data-switchChart') == 'true') {
                //window.tradingChart.update('bs', window.symbol+'-yoy', true, true);

                if (window.tradingChart.removeSerie) {
                    window.tradingChart.removeSerie(window.symbol, true, true);
                    setTimeout(function () {
                        //console.log('ADDING', window.symbol + '-yoy');
                        window.tradingChart.addSerie(window.symbol + '-yoy', true, true);
                    }, 200)
                } else if (window.tradingChart.update) {
                    window.tradingChart.update({ symbol: window.symbol + '-yoy' });
                }
            }
        } else {
            $('#ftable').show();
            $('#ftable2').hide();
            if (window.tradingChart && $(this).attr('data-switchChart') == 'true') {
                //window.tradingChart.update('bs', window.symbol, true, true);

                if (window.tradingChart.removeSerie) {
                    window.tradingChart.removeSerie(window.symbol + '-yoy', true, true);
                    setTimeout(function () {
                        console.log('ADDING', window.symbol);
                        window.tradingChart.addSerie(window.symbol, true, true);
                    }, 200)
                } else if (window.tradingChart.update) {
                    window.tradingChart.update({ symbol: window.symbol });
                }
            }
        }

        $('.markets-switch').find('a').removeClass('active');
        $(this).addClass('active');
    });

    Sortable.init();

    //shows financials tab if there are links
    var financialsLinks = document.getElementById("financialsList")

    if (financialsLinks) {
        var financialsTab = $('a[href="#financialsList"]');
        if (financialsTab) {
            $(financialsTab[0]).closest("li").removeClass("hidden");
        }
    }

});

$(document).ready(function () {
    console.log("Heatmap2");
    //if (!IsDarkMode && window.location.pathname.length <= 1 ) return
    var IsDarkModeByForce = false;
    if (window.location.pathname.length <= 1 || window.location.pathname.indexOf("matrix") > -1) IsDarkModeByForce = true;

    function shadeColor2(color, percent) {
        var f = parseInt(color.slice(1), 16), t = percent < 0 ? 0 : 255, p = percent < 0 ? percent * -1 : percent, R = f >> 16, G = f >> 8 & 0x00FF, B = f & 0x0000FF;
        return "#" + (0x1000000 + (Math.round((t - R) * p) + R) * 0x10000 + (Math.round((t - G) * p) + G) * 0x100 + (Math.round((t - B) * p) + B)).toString(16).slice(1);
    }
    function colorMyTable() {
        console.log("Heatmap2 - ColorMyTable")
        var table = $(".table-heatmap");
        var tbody = table.find('tbody');
        var _columns = {};
        table.find('thead').find('tr').find('th').each(function (i, th) {
            if ($(th).attr('data-heatmap')) {
                _columns[i] = {
                    'index': i,
                    'limit': $(th).attr('data-heatmap-limit'),
                    'upColor': $(th).attr('data-heatmap-upcolor') || '#f93639',
                    'dnColor': $(th).attr('data-heatmap-dncolor') || '#34d65f'
                };
            }
        });
        $(tbody).find('tr').each(function (i, row) {
            var tdArr = $(row).find('td');
            for (var key in _columns) {
                var thisColumn = _columns[key], td;
                if (!(td = tdArr[key])) {
                    continue;
                }
                var value = $(td).attr('data-heatmap-value') || Number($(td).html().replace('%', ''));
                if (!value) {
                    //console.log("no value ...", key);
                    continue;
                }
                var range = Math.floor(Math.abs(value) / (thisColumn.limit / 10));
                if (range > 10) range = 10;
                var percentToShade = (10 - range) / 10;
                if (TEComparable == -1 || TEComparable == 2) { thisColumn.upColor = '#34d65f'; thisColumn.dnColor = '#f93639'; }
                var color = shadeColor2(value < 0 ? thisColumn.upColor : thisColumn.dnColor, percentToShade);
                //console.log(value, percentToShade)
                if (IsDarkMode || IsDarkModeByForce) {
                    if ($(td).children().length > 0) $(td).children().css({ 'color': color });
                    else $(td).css({ 'color': color });
                }
                else $(td).css({ 'background-color': color});
            }
        });
    }
    colorMyTable();
    setInterval(colorMyTable, 60 * 1000);
});

    $(document).ready(function () {
        TableMinimizer = {
            init: function () {
                $('.table-minimize').each(function (i, item) {
                    var element = $(item),
                        elementHeight = element.outerHeight(),
                        maxHeight = element.attr('data-minimizer-height') ? Number(element.attr('data-minimizer-height')) : 300;
                    //console.log('table height:', elementHeight, 'maxHeight: ', maxHeight);
                    if (elementHeight <= maxHeight) {
                        return;
                    }

                    //apply minimizer
                    var step = element.attr('data-minimizer-step') ? Number(element.attr('data-minimizer-step')) : 300,
                        stepCount = 1;
                        icon = '',
                            mask = $('

Show More '+icon+' ');
                    element.append(mask);
                    element.css({
                        height: maxHeight
                    });

                    mask.on('click', function (e) {
                        stepCount++;
                        var newHeigh = stepCount * step;
                        //SHOW ALL
                        element.css({
                            height: elementHeight
                        });
                        mask.hide();
                        return;
                        if (newHeigh > elementHeight) {
                            element.css({
                                height: elementHeight
                            });
                            mask.hide();
                            return;
                        }
                        element.css({
                            height: newHeigh
                        });
                    });

                });
            }
        };        
        TableMinimizer.init();
    });

