                        modalFooter += "

Learn More";
                        modalFooter += "Pricing";
                        modalFooter += "" + (IsMobile ? "Contact" : "Questions? Contact us") + "";
                        modalFooter += loginAction;
                        modalFooter += ""

                        var gCategory = URL;
                        if (indicatorPage) gCategory = 'Dropdown';
                        else if (calendarPage) gCategory = 'Calendar';
                        else if (snapshotPage) gCategory = 'Snapshot';
                        ga('send', 'event', 'Data Request', gCategory, 'csv');
                        console.log('@pagemmenu recorded click in GA for: ' + gCategory)

                    }
                    break;
                case "excel":
                    modalTitle = "Trading Economics Excel Add-in";
                    modalContent += "The Trading Economics Excel Addin allows users with an active subscription to access our data directly from excel. "
                    modalContent += "Trading Economic provides more than 20 million economic indicators for nearly 200 countries, plus exchange rates, government bond yields, stock indexes, and commodity prices. ";
                    modalFooter = "

Learn More";
                    modalFooter += "Download Addin";
                    modalFooter += "Questions? Contact us";
                    if (!isLoggedIn)
                        modalFooter += loginAction;
                    modalFooter += "";

                    // ga event for Excel click
                    ga('send', 'event', 'Data Request', 'Dropdown', 'excel');
                    console.log('@pagemmenu recorded click in GA for excel')

                    break;
                case "api":
                    modalTitle = "Trading Economics API";
                    modalContent = "
